package mathmatic;

public class IntInt {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = -75;
		int b = 30;
		double c = 0;
		
		c += (double)a/b;
		System.out.println("-75 + 100 is " + (a+100));
		System.out.println((double)25/(double)b);
		System.out.println("c is " + c);
	}

}
